# W8A8 Quantization Simulation Layer (Dequantize-then-Multiply version for both W and A)
# ==========================================================================================
class W8A8Linear(nn.Module):
    """
    W8A8 Quantized Linear Layer for Inference.

    FORWARD PASS MODIFIED: This version simulates the full W8A8 quantization error.
    1. Weight is dequantized to float.
    2. Activation is quantized and then immediately dequantized to float.
    3. A standard float matrix multiplication is performed on the two resulting tensors.
    """

    def __init__(self, in_features: int, out_features: int, bias: bool = True):
        super().__init__()
        self.in_features = in_features
        self.out_features = out_features

        # self.weight = nn.Parameter(
        #     torch.empty(out_features, in_features, dtype=torch.bfloat16),
        #     requires_grad=False
        # )

        self.weight = nn.Parameter(
            torch.empty(out_features, in_features, dtype=torch.int8),
            requires_grad=False
        )
        self.weight_scale = nn.Parameter(
            torch.empty(out_features, 1, dtype=torch.float32),
            requires_grad=False
        )

        if bias:
            self.bias = nn.Parameter(torch.empty(out_features, dtype=torch.bfloat16), requires_grad=False)
        else:
            self.register_parameter('bias', None)

    def forward(self, x: torch.Tensor) -> torch.Tensor:
        """
        Simulates W8A8 quantization effect before performing float matmul.
        """
        input_dtype = x.dtype  # e.g., torch.bfloat16

        # --- Step 1: Dequantize Weight ---
        weight_dequant = self.weight.to(torch.float32) * self.weight_scale.to(torch.float32)
        weight_dequant = weight_dequant.to(torch.bfloat16)
        # # --- Step 2: Quantize and then Dequantize Activation ---
        # act_scale = x.abs().max(dim=-1, keepdim=True).values.to(torch.float32) / 127.0
        # act_scale = act_scale.clamp(min=1e-5).to(input_dtype)
        #
        # x_quant = (x / act_scale).round()
        # x_dequant = x_quant * act_scale

        # --- Step 3: Perform Float Matmul with Both Dequantized Tensors ---
        # All inputs are now in the same dtype
        # weight_dequant = self.weight
        x = x.to(torch.bfloat16)
        output = F.linear(x, weight_dequant, self.bias.to(input_dtype) if self.bias is not None else None)

        return output

    def extra_repr(self) -> str:
        return f'in_features={self.in_features}, out_features={self.out_features}, bias={self.bias is not None}'


